import { auth } from "./components/firebaseconf";
import { signInWithEmailAndPassword } from "firebase/auth";
export default {
  data() {
    return {
      vmodelname: "",
      vmodelpassw: ""
    };
  },
  methods: {
    loginBtn(name, passw) {
      this.logİn(name, passw).then(sonuc => {
        console.log(sonuc);
        this.$router.push("/home");
        //if(sonuc){}
      }).catch(hata => {
        this.bildirimGoster(hata.code + " mesaj");
      });
    },
    async logİn(email, password) {
      const a = await signInWithEmailAndPassword(auth, email, password);
      /*kullanıcıya ait bilgiler içinde */
      if (a) {
        console.log(a.user.accessToken);
        this.$router.push("/home");
      }
      return a;
    },
    bildirimGoster(msj) {
      alert(msj);
    },
    inputcontrol(name, passw) {
      if (name !== "" && passw !== "") {
        this.loginBtn(name, passw);
      } else {
        this.bildirimGoster("Tüm alanları doldurunuz");
      }
    }
  }
};
