import { createRouter,createWebHistory } from "vue-router";
import home from '../homes/Home.vue'
import listem from '../listem/listes.vue'
import logintemp from "../../view/login/logintemplate.vue";
import {loginKontrol} from '../../view/login/login.js';

    let sonuc=loginKontrol()

const route=[
  {path: '',component :logintemp},
  {path: '/home',component :home,beforeEnter: (to, from, next) => { 
    if (sonuc){ next() } else {window.location.href="/"}   }},
  {path: '/listem',component :listem, beforeEnter: (to, from, next) => {
    if (sonuc){next() } else {window.location.href="/"}
  } }
]
 const router=createRouter({
   
    history:createWebHistory(),
    routes:route
    
})

export { router}
